INSERT INTO `book` VALUES ('00bb76027244ff07f0b91601', '同人', '笑傲江湖之小师弟', '某命运夜的黑崎当麻', 'qidian', '14', '2015-10-25 18:18:09', '', '岳不群：不得了啊不得了，你有道灵光从天灵盖喷出来！\n你知道嘛，年纪轻轻的就有一身横练的筋骨，简直百年一见的练武奇才啊！\n如果能割了什么没用的东西，你还不飞龙上天？\n正所谓你不入地狱，谁入地狱……\n杨乐：\n师傅，守护世界和平这种重任······\n还是交给大师兄吧，他肯定愿意干！\n不管你割不割，反正我是不割的。', '武侠', '5', null, '第二十章 五仙教主', '34526', '1', '1', '553783a8277898e04ac211f0', '1', '2015-10-25 20:43:06');
