INSERT INTO `book` VALUES ('00f3c64adeeab438a47ea2f9', null, '超级兵王在校园', '洪七', 'chuangshi', null, '2015-10-25 19:52:01', 'http://img1.chuangshi.qq.com/upload/cover/20140616/cb_539e791a3cdad.jpg', '爱国爱家爱师妹，防火防盗防林风。 善泡妞，会学习，懂医术，练武功……他是白狼部队“狼王”特种兵，他是特种兵中的全能高手，奇葩天才！ 重生成纨绔学生，林风依旧玩世...', null, '38', '46', '第一三六六章 放弃吧！', null, null, null, '53d0dffc690db9e47ccee4ac', '0', '2015-10-25 20:43:06');
