INSERT INTO `book` VALUES ('08c6cff9f17b9cbaed8d4924', '重生,武侠,奇遇', '重生尹志平', '喝酒烫头', 'cmread', '258', '2015-10-25 18:18:50', 'http://wap.cmread.com/r/cover_file/1078/401161078/20141110101602/cover180240.jpg', '穿越成尹志平后发现……神马！小龙女居然还没出生？！那么，我就只好先去会会李莫愁、穆念慈、黄蓉……', '武侠', '18', null, '第二百三十四章：轮回（大结局）', '816200', '1', '1', '540ea019dc9bc6fb6d31105e', '1', '2015-10-25 20:43:18');
INSERT INTO `book` VALUES ('08e88bb603ce96bd2c0fa578', null, '黑道学生8：天门一哥', '郭雨轩', 'zhulang', null, '2015-10-25 20:08:22', 'http://images.zhulang.com/www/image/no_book.gif', '郭刑一，天门新一代佼佼者。江湖人称一哥！他曾经说过这么一句话：“无论我郭刑一是不是天门的人，丧尸强永远是我老大！”（黑道归来，跪求收藏。）', null, '13', null, '063（狂呼酣战）', null, null, null, '54a63eb28f48b89733136b70', '0', '2015-10-25 20:43:18');
