INSERT INTO `book` VALUES ('0ffe284c05d04bff47b5f021', '武侠', '九转战神', '沙漠水手', 'cmread', '245', '2015-10-25 18:27:01', 'http://wap.cmread.com/r/cover_file/7004/376127004/20120906095125/cover180240.jpg', '灵气大陆，一个尚武的大陆，神秘而充满诱惑。一个身怀怪病的小乞丐，终生注定不能修炼。天不亡他，一场意外，长空破音，让他识得天地奥妙，掌握天地之力，面对六大家族的权威，他不屈不挠，灵塔之战他力拔群雄，噬魂角逐他勇夺巅峰。且看小乞丐崛起之路，重掌战神府。', '武侠', '1', null, '第245章 大陆止天上始（大陆篇完结）', '594700', '0', '1', '56224096bd86e80d61893fc7', '1', null);
