INSERT INTO `book` VALUES ('10a1f85047e33b1bd8cee27d', '武侠', '幻想仙武', '小虫冲', 'qidian', '40', '2015-10-25 18:27:02', '', '孟凡冤死后，上天给了他一个愿望，不过要实现愿望的前提是完成任务。让他在一个个武侠，仙侠世界历练。凭借这不知道看了多少片子，本以为过的很好，当经历的时候瞬间凌乱。\n韦小宝变成了女人，那她还怎么娶七个老婆。\n聂风和步惊云竟然是师兄妹关系。\n凌乱的剧情让他感到无力。', '武侠', '7', null, '第三十九章转机', '125707', '1', '1', '5613cd08416d7f957568836f', '1', null);
