INSERT INTO `book` VALUES ('17da0ae4827fefb477089b18', null, '都市绝品高手', '南山向阳', 'chuangshi', null, '2015-10-25 20:10:56', 'http://img1.chuangshi.qq.com/upload/cover/20150531/cb_556a82cf28b2b.jpg', '极品高手奉命守护未婚妻，却不曾想刚归到都市便立刻陷入到一场阴谋的漩涡之中。为了保护自己的未婚妻，他神挡杀神，佛当灭佛，神威不可匹敌！', null, '6', null, '第三百零三章 大结局', null, null, null, '55a634cd6e95089674b8fc2d', '0', null);
