INSERT INTO `book` VALUES ('1d4d692285f12e17717fcd38', '王爷,武侠,封疆裂土,古代,勇敢', '精武门', '格斗家', 'qidian', '206', '2015-10-25 18:28:16', 'http://image.cmfu.com/books/99311/99311.jpg', '推荐一本超级好看的都市小说《无敌造人系统》，书号：2618375\n主角不小心被来自未来的造人系统击中，穿越到平行世界。\n造人系统很强大，可惜不是跟女孩一起造人的，而是让主角自己造人。好奇的主角随手乱点之下，结果造出了李小龙…还是强化版的完美李小龙……\n发达了，能随便造出超级牛人，干脆做经纪人，造出强化版张伯伦、乔丹大神去混NBA；又或是造出强化版的罗纳尔多、梅西去踢足球；又或是造出强化版的阿里、泰森去打拳击；又或是……\n等等，干吗光造男的？为何不造奥黛丽？赫本或是玛丽莲？梦露呢？对了，还有最爱的红姑与小倩，嘿嘿……', '武侠', '6', null, '第一百八十九章 最后一颗子弹（结束曲）', '870914', '0', '1', '5273a980291cb8d72b0033b7', '1', null);
