INSERT INTO `book` VALUES ('242a8807fae834517ab00b7d', '斗争,历史', '南宋战神', '云海之城', 'qidian', '6', '2015-10-25 18:28:41', 'http://image.cmfu.com/books/1796301/1796301.jpg', '南宋末年危机四伏，外有蒙古虎视眈眈，内有奸臣贾似道。已走败亡之路的南宋，却意外的迎来了，附身在忠王赵祺身上的三国第一猛将吕布。\n面对外忧内患的南宋，且看三国第一猛将如何再起烽火力澜狂来，一切尽在本书中！！！！！', '历史', '0', null, '第六章 初显身手（下）', '23041', '1', '1', '54afedf95cd765d90dee94e8', '1', null);
