INSERT INTO `book` VALUES ('43812ae9795cb5fbe0263700', '随身流,科幻,未来世界,升级文,争霸流', '冰封战神', '北冰羊', 'chuangshi', '1', '2015-10-25 18:31:04', 'http://wfqqreader.3g.qq.com/cover/t5_default.jpg', '在后冰川时代，最寒冷的不是冰雪，而是幸存者们的冷酷、麻木和愚昧的人性，在这个冷酷的世界里只有比别人更冷酷才能生存下来……\n一个卑微的基因人，在一系列的奇遇冒险后，获得吞噬系统与随身武器系统，拥有这2大神技，让他在冰封世界异军突起，江山红颜，皇图霸业，他踏着无数敌人的尸体，逆天而上，走向无敌战神之路！', '科幻', '0', null, '第1章 后冰川时代', '3543', '1', '1', '551e88ff0ff55bdd1fd9f711', '1', null);
