INSERT INTO `book` VALUES ('467df3f3db1d0848e17b40d3', '都市生活,都市', '门第', '战神是耶', 'w17k', '1', '2015-10-25 18:31:17', 'http://img.17k.com/images/bookcover/default_cover1.jpg', '两个门第差异极大的年轻人，抱着美好的信念走进婚姻殿堂，\n觉得结婚前的一切都挺美好的，但结婚以后由于价值观的不同，\n婚姻变得不再是童话，充满冷战和硝烟。戏剧的冲突具有一定的典型意义，\n是要门当户对还是坚持爱情打破门第。', '都市', '3', null, '第一章:指腹为婚', '1667', '1', '1', '559a0608e5eb591d02c8fa9b', '1', null);
