INSERT INTO `book` VALUES ('60d583838bbd81f69f0f4392', '同人', '笑傲之宅侠', '马踏联营', 'zhulang', '52', '2015-10-25 18:33:46', 'http://images.zhulang.com/book_cover/image/23/10/231083.jpg', '陆尘;“什么？我穿越了？”\n陆尘：“什么？我穿到笑傲江湖的世界了？”\n陆尘：“什么？我是那个爱玩猴子的陆大有？”\n陆尘：“什么？我会被劳德诺一指头干掉？”\n陆尘：“什么？还有一个人也穿越来了？还是牛叉无比的逍遥派传人？还得了九阳神功？”“好吧，江湖如此危险，我还是老老实实的当我的宅男好了，每天打坐练气益寿延年才是正经，行侠仗义什么的还是交给别人吧。最后，珍爱生命，远离令狐！”', '武侠', '4', null, '第五十章 与仪琳在一起的日子', '179453', '1', '1', '538fc50af472d87817a288b4', '1', null);
