INSERT INTO `book` VALUES ('69ba2ccf6564f61fddb76d87', null, '伪装者之棋局', '安乐日子', 'jjwxc', null, '2015-10-25 20:10:25', 'http://bbs.skyworthbox.com/data/attachment/forum/201509/02/123654g9vsmnuuquvvv2uv.jpg', '前世，她是亡国皇族，倾尽己身之才，却终难逆转天意。逃命之时被他算计被捕，她不甘心也不愿就此承认自己的失败，便向阎罗求取以来世的福禄和寿命来换取与他一世的对垒。未...', null, '5', null, '第十六章', null, null, null, '561f264312c45c86026a6c45', '0', null);
