INSERT INTO `book` VALUES ('6b6a315482975a7553515a32', '玄幻奇幻,凡人,东方玄幻', '霹雳战神', '教皇一世', 'w17k', '28', '2015-10-25 18:34:25', 'http://img.17k.com/images/bookcover/default_cover1.jpg', '人龙战士、大灭三界这句流传两千多年的上古谶言，因为一位伟大英雄的诞生而震惊三界神魔！传言，这位伟大的英雄将凭借其通天彻地的本领横扫三界！上天界，夺仙宝，战胜五祖七圣。闯龙宫，得神兵，摧毁海外仙山。入地狱，降鬼魔，释放万千恶鬼！他将给三界带来难以承受的血腥杀戮，甚至毁灭整个世界！', '奇幻', '1', null, '该章节已被锁定', '83162', '1', '1', '55e300ede4c7e3af25f427ad', '1', null);
