INSERT INTO `book` VALUES ('6ea1eb37672a64ef024b4aa7', '玄幻,张狂,宝物,升级文,个人武力,东方玄幻', '圣武战神', '零度星辰', 'chuangshi', '26', '2015-10-25 18:34:42', 'http://wfqqreader.3g.qq.com/cover/t5_default.jpg', '纵横无尽圣域，唯吾傲视天穹！\n周炎，一个天外来客，穿越成为家族支系族人；身负血海深仇，落魄潦倒，苦无出头之日；他终不甘心向命运低头，向天道发起挑战！\n他不是天才，但是所谓的天才在他面前什么都不是……\n……\n觉得还凑合，请顺手收藏一下吧！谢谢啦！O(∩_∩)O', '玄幻', '0', null, '第二十六章 我的解释', '80026', '1', '1', '54b75c19d74b81394065ca79', '1', null);
