INSERT INTO `book` VALUES ('71e76af325af198f7ae3c592', '都市,穿越,法宝,异术超能,商战,位面', '位面商人', '末日战神', 'qidian', '664', '2015-10-25 18:34:49', 'http://image.cmfu.com/books/3347275/3347275.jpg', '买卖买卖！你买我卖！\n从地球进口的粮食哦！亲！你要不是要来几斤？不贵，只需要一个金币！\n来来来，看看上好的雪盐，绝对是你没见过的珍品！有身份的人必备。\n这就是一个人得到了穿越位面的法宝，从而过上万恶的封建领主的日子的故事！\n大家收藏啊，已经上架求推荐，求打赏，求订阅！！\n闻君有保底月票一张，色泽艳丽，功效非凡，不胜心向往之，今夜当踏月来求，君素雅达，必不至令我徒劳往返。', '都市', '43', '33', '第一七九章 一堆糟心事', '1796439', '1', '1', '5481eeeff15849f84d13e7c2', '1', null);
