INSERT INTO `book` VALUES ('7688f68dc6b3eb19d9ff67cc', null, '御天成圣', '宋未明', 'zongheng', null, '2015-10-25 20:09:40', 'http://static.zongheng.com/upload/cover/2015/05/1432214590002.jpg', '少年行走在黑暗的边缘，身在长夜而心向光明，终成不朽传奇！ 一个生活在现代的大师级杀手穿越到异世，再次成为一名传说中的刺客！这是一个强者为尊、修行者占据巅峰的世界...', null, '5', null, '第15章 看一座石碑（下）', null, null, null, '5571480f0dfee8ab53bd36cf', '0', null);
