INSERT INTO `book` VALUES ('7b5838871ef2489609334679', '玄幻,热血,架空,升级练功,争霸,东方玄幻', '战神变', '小刀锋利', 'qidian', '724', '2015-10-25 18:35:23', 'http://image.cmfu.com/books/2104762/2104762.jpg', '好强的少年滕飞，在河边捡到了一个不食人间烟火，像仙女一样的白衣女子，这个女子给他带来了强大的源泉，开启了强者之路的大门。\n师父可以跟徒弟结婚吗？滕飞在努力，为了师父，为了自己！', '玄幻', '16', null, '完本感言以及新书。', '2267713', '0', '1', '50adef845da9cfc66300025c', '1', null);
