INSERT INTO `book` VALUES ('821c89c31161307fac3e6642', null, '平步青云', '骑鹤人', 'zongheng', null, '2015-10-25 20:08:39', 'http://static.zongheng.com/upload/cover/2015/07/1437446763583.jpg', '醒掌天下权，如履薄冰；醉卧美人膝，如临深渊。韩立诚重生后，便遭遇困境，巧妙化解后，从乡镇起步，一步一个脚印，最终走向人生巅峰。他坚信——掌权不忘放权，铺路不忘后...', null, '14', null, '第247章  韩立诚好事难成', null, null, null, '5594f02a62a8ba85714be6a7', '0', null);
