INSERT INTO `book` VALUES ('8632036e38af76338c4e7cb8', '玄幻,异世大陆', '第八战神', '三千星尘.', 'zhulang', '3', '2015-10-25 18:36:20', 'http://images.zhulang.com/www/image/no_book.gif?1442451113', '这是一个热血而魔幻的世界\n这是一片光明与黑暗碰撞的大地\n这是一首背负着友情和爱情的史诗\n堕落与杀戮，鲜血与荣耀\n亡者的叹息，生者的挽歌\n当昔日的辉煌消失，当绝望的泪水流下\n在这片被神遗忘的世界里\n我将用这双手\n来开创属于我们自己的未来！', '玄幻', '0', null, '第二章 天使酒吧', '11500', '1', '1', '55fa0ea9ef9b2dc37ec56304', '1', null);
