INSERT INTO `book` VALUES ('86ed8d119cc3c4247fba9a87', null, '李逵日记', '武林书生', 'w17k', null, '2015-10-25 20:08:39', 'http://img.17k.com/images/bookcover/2012/762/3/152557.jpg', '李逵的流水帐，风趣而幽默。从在梁山排座次开始，直到起义失败喝下毒酒而告终。过程爆笑不断，征方腊、田虎、王庆的事迹出人意料。流水帐里，李逵成了武林高手，与欧阳修、...', null, '7', null, '后记', null, null, null, '54a6939a3df915690b55edba', '0', null);
