INSERT INTO `book` VALUES ('89e97212f47b87c284874dec', '武侠', '武当七女', '还珠楼主', 'kanunu', '4', '2015-10-25 18:36:36', 'http://img5.douban.com/lpic/s2635148.jpg', '话说《武当异人传》说到林绿华见崔晴与妖人大战，妖人势盛，崔晴似有不敌之势，越看越觉可虑。二人本是三生爱侣，况当大难临身，祸福相共之际，绿华关心情急，哪还再计利害。急喊得一声：“晴哥休慌，我来助你。”口中说话，人早朝前飞去。崔晴全仗飞剑法宝防身，人又机智，动作神速，一上来先把迎面飞来的妖党杀死。', '武侠', '3', null, '第 四 回 万里渡关山 劫后精魂 仍依爱侣 千重消血雨 怀中灵符 忽化虹飞', null, '0', '1', '54922bbf12e223131f900f38', '1', null);
