INSERT INTO `book` VALUES ('9f3618d593e81478b153fc34', '逆天改命,英雄,杀人灭族,内劲', '金手书生', '诸葛青云', 'kanunu', '20', '2015-10-25 18:38:56', 'http://img3.douban.com/lpic/s3008832.jpg', '一面紫旗，一面黑旗，一面黄旗！这三面旗儿，是插在“太行山五丁峡”的一片小山坡上。紫旗上绣有一条生翅蜈蚣，黑旗上绣有一截手指，黄旗上则绣有一支巨笔！在插有三面旗儿的峭壁之下，陈设着一只巨锅，锅下干柴毕剥，火光熊熊，锅中沸油滚滚，油香四溢。锅口有圆桌面般大小，足可煮下一个人呢！围着这只巨锅，坐有三人，一个是中年黄衣秀士，一个是黄袍老道，另一个则是瘦削枯干的紫衣老叟！', '武侠', '6', null, '第二十章 决斗小孤山', null, '0', '1', '54922a2d12e223131f900ef3', '1', null);
