INSERT INTO `book` VALUES ('9fa70020487c8d67ea14126a', '民国,武侠,历史', '江湖怪异传', '平江不肖生', 'kanunu', '22', '2015-10-25 18:38:58', 'http://img3.douban.com/lpic/s4167042.jpg', '目录\n江湖异人传\n何包子\n秦鹤歧\n神针\n梁懒禅\n杨登云\n无名之英雄\n孙禄堂\n快婿断指\n无锡老二\n鬎福生\n黑猫与奇案\n岳麓书院之狐异\n恨海沉冤录\n一个三十年前的死强盗\n皋兰城楼上的曰猿\n虾蟆妖\n三个猴儿的故事\n能与虎\n三掌皈依记\n侠盗大肚皮\n绿林之雄\n没脚和尚\n喜鹊曹三\n近代侠义英雄传（节选）\n附录：我研究拳脚之实地练习\n平江不肖生小传', '武侠', '3', null, '第二十一章 结语', null, '0', '1', '54922b3512e223131f900f20', '1', null);
