INSERT INTO `book` VALUES ('a8248bfb8e7ed0c99aad8b7a', null, '洪荒古神', '五王子1', 'w17k', null, '2015-10-25 20:09:39', 'http://img.17k.com/images/bookcover/2014/4703/23/940629-1417223560000.jpg', '上古一战，惊天动地，将天打断为六截。 无数年月后，一代少年玄天，手持兵器碎片在最底层的天地出生。且天生有残缺，患有头痛病症，大脑中十分怪异。 终于有一天，脑海中...', null, '8', null, '第七百二十八章 朽木', null, null, null, '540dc6d320362d0006e30356', '0', null);
