INSERT INTO `book` VALUES ('a9d2bc054d6b7707049c8d39', '玄幻,异世大陆,废柴公子', '旷世战神', '枯墨游侠', 'zhulang', '9', '2015-10-25 18:39:41', 'http://images.zhulang.com/www/image/no_book.gif', '唯有在血腥中博斗！才能平静我内心的躁动！姜毅，一起一落。到底哪里才是我的战场，南域？宇宙？还是逆次元？又或者是闻所未闻的未知地方......来吧！所有我的敌人！《星辰绝》不出！谁与我争锋！！！', '玄幻', '0', null, '第十二章 把酒言欢，今夕何年？', '24537', '1', '1', '54afe5156cfe56805609b1a1', '1', null);
