INSERT INTO `book` VALUES ('ad9e173040af24d67955c3a9', '科幻', '超时空战神', '极限幻想', 'zhulang', '13', '2015-10-25 18:39:55', 'http://images.zhulang.com/www/image/no_book.gif', '25世纪人类战神因遭遇远古种族——先人，被基因改造，用于执行宇宙安全守则的执行官——缉罪者，成为了能穿越时间和空间的超时空战士，他有最变态的身体，最超级的武器......但是面对宇宙中的各种文明他该如何？邂逅一位又一位美女，那个才是他的真爱；与宇宙恐怖的恶魔——虚空魔灵的对决一触即发，他发誓：“全宇宙可以没有我，但不能没有文明，更不能没有生命！我陈浩以缉罪者之名正式向你们开战！”缉罪者——陈浩挺起了胸膛，紧握除魔的双刃，背负全宇宙的生命，游走整个宇宙，以战神之躯用缉罪之名保佑着宇宙这片净土', '科幻', '0', null, '十三章 时空仪的妙用', '30314', '1', '1', '54bcadb18b882bfa234e7b91', '1', null);
