INSERT INTO `book` VALUES ('b3aec07774d45405c2b52471', '穿越,武侠,传统武侠', '武道伐仙', '我大师兄', 'qidian', '55', '2015-10-25 18:40:38', 'http://image.cmfu.com/books/3608016/3608016.jpg', '老子曰：万物有阴阳。武侠也是如此！武侠何为阴？武侠又何为阳？\n大师兄曰：阳，属实质存在，如少林扫地僧！出手给人造成难以泯灭的印象。属佛\n大师兄曰：阴，虚无缥缈，如逍遥子未实质出手。但三个弟子足以证明可怕。属道\n两人一阴一阳一佛一道，让武侠具备了无限的想象空间。', '武侠', '7', null, '第五十八章 四恶散', '124736', '1', '1', '560cdf2575c0854673cbae4a', '1', null);
