INSERT INTO `book` VALUES ('b91bf8ce20956bc523b61f21', null, '末日放逐', '白银之瞳', 'chuangshi', null, '2015-10-25 20:10:33', 'http://img1.chuangshi.qq.com/upload/cover/20150727/cb_55b5e66169ca5.jpg', '我们会坚持下去……即使这个世界分崩离析，即使城市变成废墟，即使庭院长满荒草，即使公路上遍布怪物，即使，我们被自己的同类放逐……我们也要坚持下去……', null, '22', null, '第102章、去汇报情况', null, null, null, '55b6ed40e458ce3a2c8c0864', '0', null);
