INSERT INTO `book` VALUES ('c41f79108f2975ec41f5060c', '江湖,武侠,古代', '方邪真系列之破阵', '温瑞安', 'kanunu', '50', '2015-10-25 18:42:02', 'http://img5.douban.com/lpic/s3282136.jpg', '《四大名捕》是著名作家温瑞安先生的经典著作。\n小说主要讲述了无情、铁手、追命、冷血\n这“四大名捕”的故事。\n“四大名捕”都是武林中的数一数二的好手，各人有各人过人之能。人人俱有两手绝技。这四人的名宇，正如这四人的行事。\n无情：处事无情，内里留情；\n铁手：出手刚强，内里仁慈；\n追命：追案不欺，玩事不拘；\n冷血：杀敌冷血，情义热血……\n本书为系列之一。', '武侠', '5', null, '第六回 花沾唇', null, '0', '1', '549149d5338fe64c65faf41b', '1', null);
