INSERT INTO `book` VALUES ('d30b7c2e46bc354b41a04832', '奇遇,同人', '倚天之逍遥', '心悸之殇', 'zhulang', '63', '2015-10-25 18:43:35', 'http://images.zhulang.com/book_cover/image/21/63/216368.jpg', '一个刚毕业的大学生误入倚天世界中\n附身成将军之子子他会怎么样？面对元末汉臣窘迫现状怎么样？起义失败的朱元璋又会怎么样？泰山北斗的张三丰会是什么样？张无忌还会不会是原来那样？倚天众美女究竟会花落谁家？\n一切的一切都将在这里得到答案！', '武侠', '4', null, '六二章、送行', '160682', '1', '1', '54c5cda80eb5adcd1e03eae5', '1', null);
