INSERT INTO `book` VALUES ('d37131b0199ad3baba4e5fa8', '爽文,热血,架空,战神,东方玄幻', '绝地战神', '东虫', 'zongheng', '44', '2015-10-25 18:43:36', 'http://static.zongheng.com/upload/cover/2014/06/1403675983717.jpg', '玄妙异宝傍身，衍化传说生物加持己身，修习无名心法。林易一步步崛起，踩同族纨绔！斗家族长老！进入仙家圣地修习，战强悍师兄！斗野蛮师姐！纵横仙门，年轻一辈再无敌手。\n搅动八方风云，历经劫数，潜龙深渊，绝顶天下，成为一代战神。', '玄幻', '0', null, '第四十三章 强者', '117545', '1', '1', '54c4c01f71546afe0e726e91', '1', null);
