INSERT INTO `book` VALUES ('d3e5d3f96726518f7d532250', '热血,武侠', '华夏剑仙', '山中小住', 'qidian', '55', '2015-10-25 18:43:37', 'http://image.cmfu.com/books/3299521/3299521.jpg', '公元1839年9月，清道光十九年仲秋，黄山。华夏大地，正烽烟四起，在动乱的历史背景下，一群为国家兴亡而抛洒热血的英雄侠士，奔走在广阔的大江南北；清政府的腐败，鸭片战争，太平军，捻军，丧权辱国的各种条约。中华的大好河山，五千年的传统文明，社会现状，古韵古风，奇幻，撩心，壮志，尽在长篇武侠小说《华夏剑仙》。作者笔下的主人公把所有一切都联系在一起，向世人展现不一样的一片历史天地。', '武侠', '5', null, '第五十五章 有情也心伤', '165196', '1', '1', '5598c9d0f7cd483c7b4ef8a0', '1', null);
