INSERT INTO `book` VALUES ('df60daec0284f032113175c5', '经典,爽文,热血,江湖,武侠', '饮马黄河', '司马翎', 'kanunu', '16', '2015-10-25 18:44:35', 'http://img5.douban.com/lpic/s3258998.jpg', '《饮马黄河》是司马翎创作后期辉煌之作。书中将人物的武功修为与道德修养结合在一起，按照孟子所言的“浩然之气，至大至刚”创造出新意十足的“武侠气势论”，\n致使传统武功技击由“花拳绣腿”的浮光掠影中解放出来，而进入精神、心灵的玄妙境界。书中借着主角朱宗潜的无上智慧，逐步解开狼人、黑龙寨、冰宫、东厂领导人的身份之谜，将悬疑、推理运用于武侠作品中，大大增加了可读性，体现出司马翎创作后期主要的作品风格。', '武侠', '4', null, '第十八章', null, '0', '1', '549215c512e223131f900b7f', '1', null);
