INSERT INTO `book` VALUES ('e143604d303950ceb6f36c10', '玄幻,东方玄幻', '乾坤战神', '番薯的番薯', 'w17k', '3', '2015-10-25 18:44:43', 'http://img.17k.com/images/bookcover/default_cover1.jpg', '乾坤大陆。一个以武为尊。为战而存在的大陆。\n他本是天才。却遭天嫉妒。沦落成为万人之下的废物。\n他不愿就此度过一生。既然天不容我。那么我就势必逆天而行。毁灭一切阻拦一切困境。\n他的无上传承。创造无数奇迹。告诉世人。他才是天。他才是掌握一切的人。\n【等级划分：练体九阶\n武徒 武士 武师\n武宗 武尊 武皇\n武圣 武神\n。。。。。。。。】\n第一次。不好多包含。你们提出的意见我会去看的。争取改进。', '玄幻', '0', null, '第三章 战扬尘', '6876', '1', '1', '54b68eaffa58bf3140a65cce', '1', null);
