INSERT INTO `book` VALUES ('e376843a6e9c88683b790f92', '都市异能,都市,玄幻,斗法', '战神疾', '孤枫绮桐', 'w17k', '56', '2015-10-25 18:44:59', 'http://img.17k.com/images/bookcover/default_cover2.jpg', '魔族悄然崛起，揭开万年仇怨，魔噬静待破封，一统三界。\n因宿世之怨，许帅重回超时空。\n然手机程序意外附着许帅之身。时空科技发达，虽程序在身，但面对魔族重重阻击，追杀，许帅命运多舛。\n但任务在此，自我升级！自此我又有何俱哉！\n日后不再四下躲藏。\n自此抬起头来，杀他个天昏地暗，斗他个天地苍穹！\n傲视群雄，报万年世仇！遇神杀神，佛阻灭佛！君临天下，为我主宰！', '都市', '0', null, '第五十六章 小人利天瑞？', '119457', '1', '1', '5550b704fd28c8532ceb89a3', '1', null);
