INSERT INTO `book` VALUES ('e8af636c5fb81574115e2ebc', null, '大小姐的近身邪医', '韩家老大', 'chuangshi', null, '2015-10-25 20:41:29', 'http://img1.chuangshi.qq.com/upload/cover/20150522/cb_555eccc95bd21.jpg', '艳遇的对方竟然是自己的老师，萍水相逢的美少女，喊自己老公。冷漠的姐妹杀手，一次次被萧寒的折服。什么，还有京城第一美女，那是我老婆。谁敢说不是？某妖孽比了比拳头。...', null, '11', null, '第二百七十五章 我来找自己老婆的', null, null, null, '55752418f08c0d35078fd5c4', '0', null);
