INSERT INTO `book` VALUES ('ea7248a6fcd3ac323a657476', '都市', '九龙战神', '天地飘鸥', 'readnovel', '1262', '2015-10-25 18:45:55', 'http://img4.readnovel.com/incoming/book/0/429/250429_aj.jpg', '他是军方獠牙，无敌战神；他是国医圣手，九针渡厄；他是调酒大师，蜚声国际；他是一代琴王，神鬼莫测；他天生歌喉，万人空巷……他是天使，是魔鬼，一半火焰，一半海水……当他卷入江湖，流落都市，一个又一个传说将因他而生……他叫“傅宸雪”，且看他如何横空出世，缔造五百年传奇！QQ号：1172093117冷刺群：367662772绝代战神群：313104473', '都市', '5', null, '（1260）你了解我的长短', '2228285', '1', '1', '55d0881f65cfab4b595b6789', '1', null);
